<script>
import ListArea from "./ListArea";
import Share from "./Share";
import Wheel from "./Wheel";
import Winner from "./Winner";

export default {
    name: "index",
    components: {
        ListArea,
        Share,
        Wheel,
        Winner,
    },

    data() {
        return {
            winner: undefined,
            isSharing: false,
            isSpinning: false,
            title: undefined,
            items: undefined,
            url: undefined,
        };
    },

    methods: {
        share() {
            this.isSharing = true;
        },

        bodyClick() {
            if (this.winner !== undefined) {
                this.winner = undefined;
                this.$confetti.stop();
            }

            if (this.isSharing) {
                this.isSharing = false;
            }
        },

        itemsChange(items) {
            this.items = items;
            this.updateUrl();
        },

        updateUrl() {
            const searchParams = `?t=${this.title}&i=${this.items.join(",")}`;
            this.url = `${window.location.origin}/${searchParams}`;
            window.history.replaceState({}, "", searchParams);
        },

        spinWheel() {
            this.$refs.wheel.spinWheel();
        },

        spinBegin() {
            this.isSpinning = true;
        },

        spinComplete(winner) {
            this.winner = winner;
            this.isSpinning = false;
            this.$confetti.start({
                particlesPerFrame: 1,
            });
        },
    },

    mounted() {
        const searchParams = new URLSearchParams(window.location.search);

        this.title = searchParams.has("t") ? searchParams.get("t") : "Fruits";
        this.items = searchParams.has("i")
            ? searchParams.get("i").split(",")
            : ["Apple", "Banana", "Orange", "Pear", "Pineapple"];

        this.updateUrl();

        document.querySelector("body").addEventListener("click", (event) => {
            this.bodyClick(event);
        });
    },
};
</script>

